package day2

import (
	"strconv"
	"testing"
)

func Test_findKeys(t *testing.T) {
	tests := []struct {
		kpStr string
		start string
		input string
		want  string
	}{
		{
			kpStr: `123
456
789`,
			start: "5",
			input: `ULL
RRDDD
LURDL
UUUUD`,
			want: "1985",
		},
		{
			kpStr: `123
456
789`,
			start: "5",
			input: input,
			want:  "56855",
		},
		{
			kpStr: `  1
 234
56789
 ABC 
  D`,
			start: "5",
			input: `ULL
RRDDD
LURDL
UUUUD`,
			want: "5DB3",
		},
		{
			kpStr: `  1
 234
56789
 ABC 
  D`,
			start: "5",
			input: input,
			want:  "B3C27",
		},
	}
	for i, tt := range tests {
		t.Run(strconv.Itoa(i), func(t *testing.T) {
			got := findKeys(tt.kpStr, tt.start, tt.input)
			if got != tt.want {
				t.Errorf("findKeys() = %v, want %v", got, tt.want)
			}
		})
	}
}

const input = `



`
